mapper.select:
/* CommentMapper, selectAllComments */ SELECT COMMENT_ID /* 코멘트 ID */ , POST_ID /* 게시글 ID */ , NICKNAME /* 코멘트 작성자 닉네임 */ , CONTENT /* 코멘트 내용 */ , CREATED_AT /* 생성 시간 */ , MODIFIED_AT /* 수정 시간 */ FROM COMMENTS WHERE 1=1
AND POST_ID = #{postId}
ORDER BY CREATED_AT ASC

mapper.insert:
/* CommentMapper, insertComment */
    INSERT INTO COMMENTS
              (
                POST_ID
              , NICKNAME
              , COMMENT_PASSWORD
              , CONTENT
              )
         VALUES
              (
                #{postId}
              , #{nickname}
              , #{comment_password}
              , #{content}
              )

mapper.update:
/* CommentMapper, updateComment */
    UPDATE COMMENTS
       SET CONTENT = #{content}
         , MODIFIED_AT = NOW()
     WHERE 1=1

     AND COMMENT_ID = #{commentId}

mapper.delete:
/* CommentMapper, deleteComment */
    DELETE
      FROM COMMENTS
     WHERE 1=1

     AND COMMENT_ID = #{commentId}
