mapper.insert:
/* PostMapper, insertPost */
    INSERT INTO POSTS
              (
                NICKNAME
              , PASSWORD
              , TITLE
              , CONTENT
              )
         VALUES
              (
                #{nickname}
              , #{password}
              , #{title}
              , #{content}
              )

mapper.update:
/* PostMapper, updatePost */
    UPDATE  POSTS
       SET  title = #{postDto.title}
         ,  content = #{postDto.content}
     WHERE  1=1

        AND POST_ID = #{postId}

mapper.delete:
/* PostMapper, deletePost */
    DELETE
      FROM  POSTS
     WHERE  1=1

       AND POST_ID = #{postId}
